var util = require('util');
var Bleno = require('bleno');

var BlenoCharacteristic = Bleno.Characteristic;
var BlenoDescriptor = Bleno.Descriptor;

function CardCharacteristic() {
  CardCharacteristic.super_.call(this, {
    uuid: '01010101010101010101010101524742',
    properties: ['write', 'writeWithoutResponse'],
    descriptors: [
      new BlenoDescriptor({
        uuid: '2901',
        value: 'set blink(1) RGB value'
      })
    ]
  });
}

util.inherits(CardCharacteristic, BlenoCharacteristic);

CardCharacteristic.prototype.onWriteRequest = function(data, offset, withoutResponse, callback) {

};


module.exports = CardCharacteristic;